import { useEffect, useState } from "react"
import { UserDto } from "./UserDto";
import User from "./User";
import { LoadingOutlined } from '@ant-design/icons';
import { Button, Spin } from 'antd';


export default function UserList() {

    const[list, setList] = useState<UserDto[]>([]);
    const[isLoading, setIsLoading] = useState(true);

    async function myApiCall() {
        console.log("Inizio chiamata");
        setList([]);
        setIsLoading(true);
        try{
            const response = await fetch("https://reqres.in/api/users?delay=3");
            const users: UserDto[] = (await response.json()).data;
            console.log(users);
            setList(users);
        } catch(e) {
            console.log(e);
        }
        setIsLoading(false);
    }
    
    useEffect(() => {
        myApiCall();
        console.log("log");
    }, []);
    return <div>
        <h3>UserList</h3>
        {isLoading && <Spin indicator={<LoadingOutlined style={{ fontSize: 24 }} spin />} />}
        <Button type="primary" onClick={()=>{myApiCall()}}>Refresh</Button>
        {list.map((user,index) => <User key={index} user={user} />)}
    </div>
}
