import React, { useState } from 'react';

interface Props {
  onAdd: (newItem: string) => void;
}

export default function ListItemAdder({ onAdd }: Props) {
  const [newItem, setNewItem] = useState<string>('');

  function changingItem(ev: React.ChangeEvent<HTMLInputElement>) {
    setNewItem(ev.target.value);
  }

  function submitItem() {
    if (newItem.trim() !== '') {
      onAdd(newItem);
      setNewItem('');
    }
  }

  return (
    <div>
      <input type="text" placeholder="Add new item" value={newItem} onChange={changingItem} />
      <button onClick={submitItem}>Add</button>
    </div>
  );
}
