import { useEffect, useState } from "react"
import { UserDto } from "./UserDto";
import User from "./User";
import { LoadingOutlined } from '@ant-design/icons';
import { Spin } from 'antd';


export default function UserList() {

    const[list, setList] = useState<UserDto[]>([]);
    const[isLoading, setIsLoading] = useState(true);
    
    useEffect(() => {
        async function myApiCall() {
            console.log("Inizio chiamata");
            setIsLoading(true);
            const response = await fetch("https://reqres.in/api/users?delay=5");
            const users: UserDto[] = (await response.json()).data;
            console.log(users);
            setList(users);
            setIsLoading(false);
        }
        myApiCall();
        console.log("log");
    })
    return <div>
        UserList
        <Spin indicator={<LoadingOutlined style={{ fontSize: 24 }} spin />} />;
        {list.map((user,index) => <User key={index} user={user} />)}
    </div>
}
